mapper.select:
SELECT a.*, IFNULL(SUM(rp.`point`), 0) AS extra__sumReactionPoint, IFNULL(SUM(IF(rp.`point` > 0, rp.`point`, 0)), 0) AS extra__goodReactionPoint, IFNULL(SUM(IF(rp.`point` < 0, rp.`point`, 0)), 0) AS extra__badReactionPoint FROM ( SELECT a.*, m.nickname AS extra__writerName FROM article a LEFT JOIN `member` m ON a.memberId = m.id WHERE 1
AND a.boardId = #{boardId}
AND a.title LIKE CONCAT('%', #{searchKeyword}, '%')
AND a.`body` LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( a.title LIKE CONCAT('%', #{searchKeyword}, '%') OR a.`body` LIKE CONCAT('%', #{searchKeyword}, '%') )
) AS a LEFT JOIN reactionPoint AS rp ON a.id = rp.relId AND rp.relCodeType = 'article' GROUP BY a.id ORDER BY a.id DESC LIMIT #{limitStart}, #{limitTake}

SELECT a.*, m.nickname AS extra__writerName FROM article a LEFT JOIN `member` m ON a.memberId = m.id
WHERE boardId = #{boardId}
ORDER BY id DESC

mapper.update:
UPDATE `article`
SET hit = hit+1
WHERE id = #{id};
