mapper.insert:
INSERT INTO `attr`
regDate = NOW(), updateDate = NOW(), relTypeCode = #{relTypeCode}, relId = #{relId}, typeCode = #{typeCode}, type2Code = #{type2Code}, `value` = #{value}
, expireDate = #{expireDate}

mapper.select:
SELECT *
FROM attr
WHERE relId = #{relId}
AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND (expireDate >= NOW() OR expireDate IS NULL)
