mapper.select:
SELECT a.*, m.nickname AS extra__writerName
FROM `reply` AS a
LEFT JOIN `member` AS m
ON a.memberId = m.id
WHERE a.relId = #{id} 
AND a.relTypeCode = #{relTypeCode} ;

SELECT *
FROM `reply`
WHERE id = #{id} 
AND relTypeCode = "article" ;

mapper.update:
UPDATE `reply`
SET updateDate = NOW(),
`body` = #{body}
WHERE id = #{id}
